import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [pets, setPets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPets = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/pets`);
        const data = await response.json();
        setPets(data.animals);
        setLoading(false);
      } catch (error) {
        console.error(error);
      }
    };

    fetchPets();
  }, []);

  return (
    <div className="App">
      <h1>Adoptable Pets</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {pets.map((pet) => (
            <li key={pet.id}>
              <img src={pet.photos.length > 0 ? pet.photos[0].medium : 'no-image'} alt={pet.name} />
              <h2>{pet.name}</h2>
              <p>{pet.description}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
